import React, { Component } from 'react';
import { Link, withRouter } from 'react-router-dom';
import MyContext from '../contexts/MyContext';
import { FaShoppingCart } from 'react-icons/fa';  
import '../css/Inform.css';

class Inform extends Component {
  static contextType = MyContext;

  render() {
    // Kiểm tra đường dẫn hiện tại
    const { pathname } = this.props.location;

    return (
      <div className="border-bottom">
        <div className="float-left">
          {this.context.token === '' ? (
            <div>
              <Link to='/login' className={`border-bottom ${pathname === '/login' ? 'active' : ''}`}>Login</Link> | 
              <Link to='/signup' className={`border-bottom ${pathname === '/signup' ? 'active' : ''}`}>Sign-up</Link> | 
              <Link to='/active' className={`border-bottom ${pathname === '/active' ? 'active' : ''}`}>Active</Link>
            </div>
          ) : (
            <div>
              Hello <b className="border-bottom">{this.context.customer.name}</b> |
              <Link to='/login' className="border-bottom logout-link" onClick={() => this.lnkLogoutClick()}>Logout</Link> |
              <Link to='/myprofile' className={`border-bottom ${pathname === '/myprofile' ? 'active' : ''}`}>My profile</Link> |
              <Link to='/myorders' className={`border-bottom ${pathname === '/myorders' ? 'active' : ''}`}>My orders</Link>
            </div>
          )}
        </div>
        <div className="float-right">
          <Link to='/mycart' className={`border-bottom cart-link ${pathname === '/mycart' ? 'active' : ''}`}>
            My cart <FaShoppingCart />
          </Link>
          <span className="have-items">have <b className="border-bottom">{this.context.mycart.length}</b> items</span>
        </div>
        <div className="float-clear" />
      </div>
    );
  }

  lnkLogoutClick() {
    this.context.setToken('');
    this.context.setCustomer(null);
    this.context.setMycart([]);
  }
}

export default withRouter(Inform);  // Dùng withRouter để truy cập pathname
