import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import MyContext from '../contexts/MyContext';
import '../css/Inform.css';

class Inform extends Component {
  static contextType = MyContext; // using this.context to access global state
  render() {
    return (
      <div className="inform-container">
        <div className="inform-content">
          <div className="inform-left">
            {this.context.token === "" ? (
              <div>
                <Link to="/login" className="inform-left">
                  Login
                </Link>
                <Link to="/signup" className="inform-left">
                  Sign-up
                </Link>
                <Link to="/active" className="inform-left">
                  Active
                </Link>
              </div>
            ) : (
              <div>
                Hello <b>{this.context.customer.name}</b>
                <Link
                  to="/home"
                  className="inform-left"
                  onClick={() => this.lnkLogoutClick()}
                >
                  Logout
                </Link>
                <Link to="/myprofile" className="inform-left">
                  My profile
                </Link>
                <Link to="/myorders" className="inform-left">
                  My orders
                </Link>
              </div>
            )}
          </div>
          <div className="inform-right">
            <Link to="/mycart" className="inform-right">
              My cart
            </Link>{" "}
            have <b>{this.context.mycart.length}</b> items
          </div>
        </div>
      </div>
    );
  }
  // event-handlers
  lnkLogoutClick() {
    this.context.setToken('');
    this.context.setCustomer(null);
    this.context.setMycart([]);
  }
}
export default Inform;
