import React, { Component } from 'react';
import { Link } from 'react-router-dom';  // Sử dụng Link thay vì NavLink
import MyContext from '../contexts/MyContext';
import { FaShoppingCart } from 'react-icons/fa';  // Import icon giỏ hàng
import '../css/Inform.css';

class Inform extends Component {
  static contextType = MyContext;

  render() {
    return (
      <div className="border-bottom">
        <div className="float-left">
          {this.context.token === '' ?
            <div>
              <Link to='/login' className="border-bottom">Login</Link> | 
              <Link to='/signup' className="border-bottom">Sign-up</Link> | 
              <Link to='/active' className="border-bottom">Active</Link>
            </div>
            :
            <div>
              Hello <b className="border-bottom">{this.context.customer.name}</b> |
              <Link to='/home' className="border-bottom logout-link">Logout</Link> |  {/* Sử dụng class logout-link */}
              <Link to='/myprofile' className="border-bottom">My profile</Link> |
              <Link to='/myorders' className="border-bottom">My orders</Link>
            </div>
          }
        </div>
        <div className="float-right">
          <Link to='/mycart' className="border-bottom cart-link">
            My cart <FaShoppingCart />
          </Link>
          <span className="have-items">have <b className="border-bottom">{this.context.mycart.length}</b> items</span>
        </div>
        <div className="float-clear" />
      </div>
    );
  }

  // event-handlers
  lnkLogoutClick() {
    this.context.setToken('');
    this.context.setCustomer(null);
    this.context.setMycart([]);
  }
}

export default Inform;
