import React, { useContext } from 'react';
import { Link, useLocation } from 'react-router-dom';
import MyContext from '../contexts/MyContext';
import { FaShoppingCart } from 'react-icons/fa';  
import '../css/Inform.css';

const Inform = () => {
  const location = useLocation(); // Dùng useLocation để lấy pathname hiện tại
  const { token, customer, mycart, setToken, setCustomer, setMycart } = useContext(MyContext);

  const lnkLogoutClick = () => {
    setToken('');
    setCustomer(null);
    setMycart([]);
  };

  return (
    <div className="border-bottom">
      <div className="float-left">
        {token === '' ? (
          <div>
            <Link to='/login' className={`border-bottom ${location.pathname === '/login' ? 'active' : ''}`}>Login</Link> | 
            <Link to='/signup' className={`border-bottom ${location.pathname === '/signup' ? 'active' : ''}`}>Sign-up</Link> | 
            <Link to='/active' className={`border-bottom ${location.pathname === '/active' ? 'active' : ''}`}>Active</Link>
          </div>
        ) : (
          <div>
            Hello <b className="border-bottom">{customer.name}</b> |
            <Link to='/login' className="border-bottom logout-link" onClick={lnkLogoutClick}>Logout</Link> |
            <Link to='/myprofile' className={`border-bottom ${location.pathname === '/myprofile' ? 'active' : ''}`}>My profile</Link> |
            <Link to='/myorders' className={`border-bottom ${location.pathname === '/myorders' ? 'active' : ''}`}>My orders</Link>
          </div>
        )}
      </div>
      <div className="float-right">
        <Link to='/mycart' className={`border-bottom cart-link ${location.pathname === '/mycart' ? 'active' : ''}`}>
          My cart <FaShoppingCart />
        </Link>
        <span className="have-items">have <b className="border-bottom">{mycart.length}</b> items</span>
      </div>
      <div className="float-clear" />
    </div>
  );
};

export default Inform;
