import axios from 'axios';
import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { FaSearch } from 'react-icons/fa';  // Import icon kính lúp
import withRouter from '../utils/withRouter';
import '../css/Menu.css';

class Menu extends Component {
  constructor(props) {
    super(props);
    this.state = {
      categories: [],
      txtKeyword: ''
    };
  }

  render() {
    const { pathname } = this.props.location;
    const cates = this.state.categories.map((item) => {
      const isActive = pathname.includes(item._id); // Kiểm tra nếu danh mục hiện tại khớp với URL
      return (
        <li key={item._id} className={`menu-item ${isActive ? 'active' : ''}`}>
          <Link to={'/product/category/' + item._id} className="menu-link">{item.name}</Link>
        </li>
      );
    });

    return (
      <div className="menu-container border-bottom">
        <ul className="menu-list">
          <li className="menu-item"><Link to='/' className="menu-link">Home</Link></li>
          {cates}
        </ul>
        <form className="search" onSubmit={(e) => this.btnSearchClick(e)}>
          <input 
            type="search" 
            placeholder="Enter keyword" 
            className="keyword" 
            value={this.state.txtKeyword} 
            onChange={(e) => this.setState({ txtKeyword: e.target.value })} 
          />
          <button type="submit" className="search-button">
            <FaSearch />
          </button>
        </form>
      </div>
    );
  }

  componentDidMount() {
    this.apiGetCategories();
  }

  btnSearchClick(e) {
    e.preventDefault();
    this.props.navigate('/product/search/' + this.state.txtKeyword);
  }

  apiGetCategories() {
    axios.get('/api/customer/categories').then((res) => {
      this.setState({ categories: res.data });
    });
  }
}

export default withRouter(Menu);
