import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import MyContext from '../contexts/MyContext';
import '../css/Inform.css';

class Inform extends Component {
  static contextType = MyContext; // using this.context to access global state
  render() {
    return (
      <div className="border-bottom">
        <div className="float-left">
          {this.context.token === '' ?
            <div><Link to='/login' className="border-bottom">Login</Link> | <Link to='/signup' className="border-bottom">Sign-up</Link> | <Link to='/active' className="border-bottom">Active</Link></div>
            :
            <div>Hello <b className="border-bottom">{this.context.customer.name}</b> | <Link to='/home' className="border-bottom" onClick={() => this.lnkLogoutClick()}>Logout</Link> | <Link to='/myprofile' className="border-bottom">My profile</Link> | <Link to='/myorders' className="border-bottom">My orders</Link></div>
          }
        </div>
        <div className="float-right">
          <img to='/mycart' className="border-bottom">My cart</img> have <b className="border-bottom">{this.context.mycart.length}</b> items
        </div>
        <div className="float-clear" />
      </div>
    );
  }
  // event-handlers
  lnkLogoutClick() {
    this.context.setToken('');
    this.context.setCustomer(null);
    this.context.setMycart([]);
  }
}
export default Inform;
